@import url('https://fonts.googleapis.com/css2?family=Plus+Jakarta+Sans:wght@300;400;500;600;700;800&display=swap');
@import "tailwindcss";

@plugin "tailwindcss-animate";

@custom-variant dark (&:is(.dark *));

@theme {
  --font-sans: 'Plus Jakarta Sans', sans-serif;
  --radius-lg: var(--radius);
  --radius-md: calc(var(--radius) - 2px);
  --radius-sm: calc(var(--radius) - 4px);

  --color-background: hsl(var(--background));
  --color-foreground: hsl(var(--foreground));
  --color-card: hsl(var(--card));
  --color-card-foreground: hsl(var(--card-foreground));
  --color-popover: hsl(var(--popover));
  --color-popover-foreground: hsl(var(--popover-foreground));
  --color-primary: hsl(var(--primary));
  --color-primary-foreground: hsl(var(--primary-foreground));
  --color-secondary: hsl(var(--secondary));
  --color-secondary-foreground: hsl(var(--secondary-foreground));
  --color-muted: hsl(var(--muted));
  --color-muted-foreground: hsl(var(--muted-foreground));
  --color-accent: hsl(var(--accent));
  --color-accent-foreground: hsl(var(--accent-foreground));
  --color-destructive: hsl(var(--destructive));
  --color-border: hsl(var(--border));
  --color-input: hsl(var(--input));
  --color-ring: hsl(var(--ring));

  /* Sidebar tokens */
  --color-sidebar: hsl(var(--sidebar));
  --color-sidebar-foreground: hsl(var(--sidebar-foreground));
  --color-sidebar-primary: hsl(var(--sidebar-primary));
  --color-sidebar-primary-foreground: hsl(var(--sidebar-primary-foreground));
  --color-sidebar-accent: hsl(var(--sidebar-accent));
  --color-sidebar-accent-foreground: hsl(var(--sidebar-accent-foreground));
  --color-sidebar-border: hsl(var(--sidebar-border));
  --color-sidebar-ring: hsl(var(--sidebar-ring));
}

:root {
  --radius: 0.75rem;

  /* Background: warm light slate */
  --background: 220 20% 97%;
  --foreground: 224 30% 12%;

  --card: 0 0% 100%;
  --card-foreground: 224 30% 12%;

  --popover: 0 0% 100%;
  --popover-foreground: 224 30% 12%;

  /* Primary: rich indigo */
  --primary: 244 75% 57%;
  --primary-foreground: 0 0% 100%;
  --primary-glow: 244 85% 72%;

  --secondary: 220 20% 92%;
  --secondary-foreground: 224 30% 20%;

  --muted: 220 15% 94%;
  --muted-foreground: 220 15% 50%;

  --accent: 38 95% 58%;
  --accent-foreground: 224 30% 12%;

  --destructive: 0 72% 55%;
  --border: 220 15% 88%;
  --input: 220 15% 91%;
  --ring: 244 75% 57%;

  /* Sidebar: deep navy */
  --sidebar: 226 45% 14%;
  --sidebar-foreground: 220 30% 85%;
  --sidebar-primary: 244 75% 65%;
  --sidebar-primary-foreground: 0 0% 100%;
  --sidebar-accent: 226 35% 22%;
  --sidebar-accent-foreground: 0 0% 100%;
  --sidebar-border: 226 35% 20%;
  --sidebar-ring: 244 75% 65%;

  /* Custom design tokens */
  --gradient-primary: linear-gradient(135deg, hsl(244 75% 57%), hsl(262 75% 62%));
  --gradient-accent: linear-gradient(135deg, hsl(38 95% 58%), hsl(28 95% 58%));
  --gradient-hero: linear-gradient(135deg, hsl(226 45% 14%) 0%, hsl(244 45% 25%) 100%);
  --gradient-card: linear-gradient(160deg, hsl(0 0% 100%), hsl(220 20% 98%));

  --shadow-card: 0 1px 3px 0 hsl(220 30% 15% / 0.08), 0 4px 12px -2px hsl(220 30% 15% / 0.06);
  --shadow-elevated: 0 4px 20px -4px hsl(244 75% 57% / 0.25), 0 8px 32px -8px hsl(244 75% 57% / 0.1);
  --shadow-glow: 0 0 30px hsl(244 75% 57% / 0.3);

  --transition-smooth: all 0.25s cubic-bezier(0.4, 0, 0.2, 1);
}

* {
  border-color: hsl(var(--border));
  box-sizing: border-box;
}

body {
  background-color: hsl(var(--background));
  color: hsl(var(--foreground));
  font-family: var(--font-sans);
  font-feature-settings: "cv02", "cv03", "cv04", "cv11";
  -webkit-font-smoothing: antialiased;
}

/* ‚îÄ‚îÄ Stat Card Variants ‚îÄ‚îÄ */
.stat-card-primary {
  background: var(--gradient-primary);
  color: hsl(var(--primary-foreground));
  box-shadow: var(--shadow-elevated);
}

.stat-card-accent {
  background: var(--gradient-accent);
  color: hsl(224 30% 12%);
}

.stat-card-default {
  background: var(--gradient-card);
  box-shadow: var(--shadow-card);
  border: 1px solid hsl(var(--border));
}

/* ‚îÄ‚îÄ KPI Icon Orb ‚îÄ‚îÄ */
.icon-orb {
  width: 2.75rem;
  height: 2.75rem;
  border-radius: 0.875rem;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
}
.icon-orb-primary { background: hsl(var(--primary) / 0.12); color: hsl(var(--primary)); }
.icon-orb-amber   { background: hsl(38 95% 58% / 0.12); color: hsl(38 85% 45%); }
.icon-orb-emerald { background: hsl(160 84% 39% / 0.12); color: hsl(160 84% 32%); }
.icon-orb-rose    { background: hsl(355 90% 58% / 0.12); color: hsl(355 80% 48%); }
.icon-orb-violet  { background: hsl(262 75% 60% / 0.12); color: hsl(262 75% 52%); }
.icon-orb-white   { background: hsl(0 0% 100% / 0.15); color: hsl(0 0% 100%); }

/* ‚îÄ‚îÄ Hero Section ‚îÄ‚îÄ */
.hero-banner {
  background: var(--gradient-hero);
  border-radius: 1.25rem;
  padding: 2rem 2.5rem;
  position: relative;
  overflow: hidden;
  color: hsl(0 0% 100%);
}
.hero-banner::before {
  content: '';
  position: absolute;
  inset: 0;
  background: radial-gradient(ellipse at 70% 50%, hsl(244 85% 72% / 0.3) 0%, transparent 60%);
}

/* ‚îÄ‚îÄ Status Badges ‚îÄ‚îÄ */
.badge-geplant    { background: hsl(220 14% 90%); color: hsl(220 20% 40%); }
.badge-aktiv      { background: hsl(160 60% 88%); color: hsl(160 84% 28%); }
.badge-abgeschlossen { background: hsl(244 60% 90%); color: hsl(244 75% 45%); }
.badge-abgesagt   { background: hsl(0 60% 92%); color: hsl(0 72% 45%); }

/* ‚îÄ‚îÄ Chart container ‚îÄ‚îÄ */
.chart-card {
  background: hsl(var(--card));
  border: 1px solid hsl(var(--border));
  border-radius: var(--radius-lg);
  box-shadow: var(--shadow-card);
  padding: 1.5rem;
}

/* ‚îÄ‚îÄ Scrollbar ‚îÄ‚îÄ */
::-webkit-scrollbar { width: 6px; height: 6px; }
::-webkit-scrollbar-track { background: transparent; }
::-webkit-scrollbar-thumb { background: hsl(var(--border)); border-radius: 3px; }
::-webkit-scrollbar-thumb:hover { background: hsl(var(--muted-foreground)); }

