@import url('https://fonts.googleapis.com/css2?family=Plus+Jakarta+Sans:ital,wght@0,300;0,400;0,500;0,600;0,700;0,800;1,400&display=swap');
@import "tailwindcss";
@import "tw-animate-css";

@custom-variant dark (&:is(.dark *));

@theme inline {
  --font-sans: 'Plus Jakarta Sans', sans-serif;
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);
}

:root {
  --radius: 0.75rem;

  /* Warm light base */
  --background: oklch(0.974 0.004 264);
  --foreground: oklch(0.17 0.025 264);

  --card: oklch(1 0 0);
  --card-foreground: oklch(0.17 0.025 264);

  --popover: oklch(1 0 0);
  --popover-foreground: oklch(0.17 0.025 264);

  /* Rich indigo primary */
  --primary: oklch(0.52 0.22 264);
  --primary-foreground: oklch(1 0 0);

  --secondary: oklch(0.95 0.006 264);
  --secondary-foreground: oklch(0.25 0.025 264);

  --muted: oklch(0.955 0.004 264);
  --muted-foreground: oklch(0.52 0.02 264);

  /* Warm amber accent */
  --accent: oklch(0.80 0.16 72);
  --accent-foreground: oklch(0.17 0.025 264);

  --destructive: oklch(0.577 0.245 27);
  --border: oklch(0.90 0.008 264);
  --input: oklch(0.92 0.006 264);
  --ring: oklch(0.52 0.22 264);

  --chart-1: oklch(0.52 0.22 264);
  --chart-2: oklch(0.65 0.18 160);
  --chart-3: oklch(0.80 0.16 72);
  --chart-4: oklch(0.62 0.20 300);
  --chart-5: oklch(0.60 0.22 25);

  /* Deep navy sidebar */
  --sidebar: oklch(0.22 0.055 264);
  --sidebar-foreground: oklch(0.80 0.02 264);
  --sidebar-primary: oklch(0.62 0.20 264);
  --sidebar-primary-foreground: oklch(1 0 0);
  --sidebar-accent: oklch(0.28 0.055 264);
  --sidebar-accent-foreground: oklch(1 0 0);
  --sidebar-border: oklch(0.28 0.04 264);
  --sidebar-ring: oklch(0.62 0.20 264);

  /* Custom tokens */
  --gradient-primary: linear-gradient(135deg, oklch(0.52 0.22 264), oklch(0.55 0.22 285));
  --gradient-accent: linear-gradient(135deg, oklch(0.80 0.16 72), oklch(0.75 0.18 55));
  --gradient-hero: linear-gradient(135deg, oklch(0.22 0.055 264) 0%, oklch(0.30 0.09 275) 100%);
  --gradient-subtle: linear-gradient(160deg, oklch(1 0 0), oklch(0.974 0.004 264));

  --shadow-card: 0 1px 3px 0 oklch(0.17 0.025 264 / 0.08), 0 4px 12px -2px oklch(0.17 0.025 264 / 0.06);
  --shadow-elevated: 0 4px 20px -4px oklch(0.52 0.22 264 / 0.25), 0 8px 32px -8px oklch(0.52 0.22 264 / 0.12);
  --shadow-glow: 0 0 30px oklch(0.52 0.22 264 / 0.3);
  --transition-smooth: all 0.25s cubic-bezier(0.4, 0, 0.2, 1);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
    box-sizing: border-box;
  }
  body {
    @apply bg-background text-foreground;
    font-family: var(--font-sans);
    font-feature-settings: "cv02", "cv03", "cv04", "cv11";
    -webkit-font-smoothing: antialiased;
  }
}

/* ‚îÄ‚îÄ Hero Banner ‚îÄ‚îÄ */
.hero-banner {
  background: var(--gradient-hero);
  border-radius: 1.25rem;
  padding: 2rem 2.5rem;
  position: relative;
  overflow: hidden;
  color: oklch(1 0 0);
}
.hero-banner::before {
  content: '';
  position: absolute;
  inset: 0;
  background: radial-gradient(ellipse at 75% 50%, oklch(0.62 0.20 264 / 0.35) 0%, transparent 60%);
  pointer-events: none;
}
.hero-banner::after {
  content: '';
  position: absolute;
  right: -2rem;
  top: -2rem;
  width: 14rem;
  height: 14rem;
  border-radius: 50%;
  background: oklch(0.62 0.20 264 / 0.12);
  pointer-events: none;
}

/* ‚îÄ‚îÄ Stat Cards ‚îÄ‚îÄ */
.stat-card-primary {
  background: var(--gradient-primary);
  color: oklch(1 0 0);
  box-shadow: var(--shadow-elevated);
  border: none !important;
}
.stat-card-accent {
  background: var(--gradient-accent);
  color: oklch(0.17 0.025 264);
  border: none !important;
}
.stat-card-default {
  background: var(--gradient-subtle);
  box-shadow: var(--shadow-card);
  border: 1px solid oklch(var(--border));
}

/* ‚îÄ‚îÄ Icon Orbs ‚îÄ‚îÄ */
.icon-orb {
  width: 2.75rem;
  height: 2.75rem;
  border-radius: 0.875rem;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-shrink: 0;
}
.icon-orb-primary  { background: oklch(0.52 0.22 264 / 0.12); color: oklch(0.52 0.22 264); }
.icon-orb-amber    { background: oklch(0.80 0.16 72 / 0.15);  color: oklch(0.60 0.18 62); }
.icon-orb-emerald  { background: oklch(0.65 0.18 160 / 0.12); color: oklch(0.45 0.18 155); }
.icon-orb-rose     { background: oklch(0.60 0.22 25 / 0.12);  color: oklch(0.50 0.22 22); }
.icon-orb-violet   { background: oklch(0.62 0.20 300 / 0.12); color: oklch(0.48 0.20 295); }
.icon-orb-white    { background: oklch(1 0 0 / 0.15); color: oklch(1 0 0); }

/* ‚îÄ‚îÄ Status Badges ‚îÄ‚îÄ */
.badge-geplant      { background: oklch(0.92 0.006 264); color: oklch(0.42 0.02 264); }
.badge-aktiv        { background: oklch(0.88 0.10 155); color: oklch(0.35 0.18 150); }
.badge-abgeschlossen { background: oklch(0.88 0.08 264); color: oklch(0.38 0.18 264); }
.badge-abgesagt     { background: oklch(0.90 0.08 25);  color: oklch(0.45 0.22 22); }

/* ‚îÄ‚îÄ Chart Card ‚îÄ‚îÄ */
.chart-card {
  background: oklch(var(--card));
  border: 1px solid oklch(var(--border));
  border-radius: var(--radius-lg);
  box-shadow: var(--shadow-card);
  padding: 1.5rem;
}

/* ‚îÄ‚îÄ Scrollbar ‚îÄ‚îÄ */
::-webkit-scrollbar { width: 6px; height: 6px; }
::-webkit-scrollbar-track { background: transparent; }
::-webkit-scrollbar-thumb { background: oklch(var(--border)); border-radius: 3px; }
::-webkit-scrollbar-thumb:hover { background: oklch(var(--muted-foreground)); }

/* ‚îÄ‚îÄ Table row hover ‚îÄ‚îÄ */
tr:hover td { background: oklch(0.974 0.004 264 / 0.6); }

