            <div className="text-sm font-bold" style={{ color: 'oklch(0.45 0.18 155)' }}>
              {stats?.anmeldungen.length ? Math.round((bezahlteAnmeldungen / stats.anmeldungen.length) * 100) : 0}%
            </div>
