:root {
  --radius: 0.5rem;

  /* Base colors ‚Äî warm off-white background */
  --background: oklch(0.982 0.003 247);
  --foreground: oklch(0.18 0.02 255);
  --card: oklch(1 0 0);
  --card-foreground: oklch(0.18 0.02 255);
  --popover: oklch(1 0 0);
  --popover-foreground: oklch(0.18 0.02 255);

  /* Primary ‚Äî deep indigo */
  --primary: oklch(0.42 0.18 264);
  --primary-foreground: oklch(0.98 0 0);

  --secondary: oklch(0.95 0.01 247);
  --secondary-foreground: oklch(0.3 0.05 264);
  --muted: oklch(0.95 0.005 247);
  --muted-foreground: oklch(0.52 0.02 255);
  --accent: oklch(0.93 0.025 264);
  --accent-foreground: oklch(0.32 0.1 264);
  --destructive: oklch(0.577 0.245 27.325);
  --border: oklch(0.9 0.008 247);
  --input: oklch(0.93 0.007 247);
  --ring: oklch(0.42 0.18 264);

  /* Charts */
  --chart-1: oklch(0.52 0.22 264);
  --chart-2: oklch(0.62 0.16 196);
  --chart-3: oklch(0.72 0.16 52);
  --chart-4: oklch(0.65 0.2 340);
  --chart-5: oklch(0.58 0.18 148);

  /* Sidebar ‚Äî deep navy */
  --sidebar: oklch(0.22 0.06 264);
  --sidebar-foreground: oklch(0.92 0.01 247);
  --sidebar-primary: oklch(0.62 0.22 264);
  --sidebar-primary-foreground: oklch(1 0 0);
  --sidebar-accent: oklch(0.3 0.07 264);
  --sidebar-accent-foreground: oklch(0.95 0.01 247);
  --sidebar-border: oklch(0.32 0.06 264);
  --sidebar-ring: oklch(0.62 0.22 264);

  /* Custom design tokens */
  --hero-bg: oklch(0.42 0.18 264);
  --hero-fg: oklch(0.98 0 0);

  --stat-indigo: oklch(0.52 0.22 264);
  --stat-teal: oklch(0.6 0.14 196);
  --stat-amber: oklch(0.72 0.18 60);
  --stat-rose: oklch(0.65 0.2 8);
  --stat-violet: oklch(0.58 0.2 300);

  --gradient-hero: linear-gradient(135deg, oklch(0.38 0.2 264), oklch(0.28 0.14 280));
  --gradient-card-indigo: linear-gradient(135deg, oklch(0.52 0.22 264), oklch(0.45 0.2 280));
  --gradient-card-teal: linear-gradient(135deg, oklch(0.58 0.15 196), oklch(0.52 0.14 210));
  --gradient-card-amber: linear-gradient(135deg, oklch(0.72 0.18 60), oklch(0.68 0.2 45));
  --gradient-card-rose: linear-gradient(135deg, oklch(0.65 0.2 8), oklch(0.6 0.22 350));
  --gradient-card-violet: linear-gradient(135deg, oklch(0.58 0.2 300), oklch(0.52 0.22 315));
  --shadow-card: 0 4px 20px -4px oklch(0.42 0.18 264 / 0.15);
  --shadow-hero: 0 20px 60px -15px oklch(0.28 0.14 280 / 0.4);
  --transition-smooth: all 0.25s cubic-bezier(0.4, 0, 0.2, 1);
}

