import { NavLink, Outlet } from 'react-router-dom';
import { BookOpen, ClipboardList, DoorOpen, GraduationCap, LayoutDashboard, Menu, Users, X } from 'lucide-react';
import { useState } from 'react';

// ‚ö° Customize these for your app
const APP_TITLE = 'Dashboard';
const APP_SUBTITLE = 'Verwaltung';

const navigation = [
  { name: '√úbersicht', href: '/', icon: LayoutDashboard },
  { name: 'Dozenten', href: '/dozenten', icon: GraduationCap },
  { name: 'R√§ume', href: '/raeume', icon: DoorOpen },
  { name: 'Teilnehmer', href: '/teilnehmer', icon: Users },
  { name: 'Kurse', href: '/kurse', icon: BookOpen },
  { name: 'Anmeldungen', href: '/anmeldungen', icon: ClipboardList },
];

export function Layout() {
  const [sidebarOpen, setSidebarOpen] = useState(false);

  return (
    <div className="min-h-screen bg-background">
      {sidebarOpen && (
        <div
          className="fixed inset-0 bg-black/50 backdrop-blur-sm z-40 lg:hidden"
          onClick={() => setSidebarOpen(false)}
        />
      )}

      <aside
        className={`
          fixed top-0 left-0 z-50 h-full w-64 bg-sidebar border-r border-sidebar-border
          transform transition-transform duration-200 ease-in-out
          ${sidebarOpen ? 'translate-x-0' : '-translate-x-full'}
          lg:translate-x-0
        `}
      >
        <div className="flex items-center justify-between px-5 py-6 border-b border-sidebar-border">
          <div className="flex items-center gap-3">
            <div className="w-9 h-9 rounded-xl bg-sidebar-primary flex items-center justify-center shadow-sm">
              <GraduationCap size={16} className="text-sidebar-primary-foreground" />
            </div>
            <div>
              <h1 className="text-sm font-bold tracking-tight text-sidebar-foreground">{APP_TITLE}</h1>
              <p className="text-xs text-sidebar-foreground/60">{APP_SUBTITLE}</p>
            </div>
          </div>
          <button
            className="lg:hidden p-1.5 rounded-lg text-sidebar-foreground/60 hover:text-sidebar-foreground transition-colors"
            onClick={() => setSidebarOpen(false)}
          >
            <X size={16} />
          </button>
        </div>
        <nav className="px-3 pt-4 space-y-0.5">
          <p className="px-3 pb-2 text-xs font-semibold uppercase tracking-widest text-sidebar-foreground/40">
            Navigation
          </p>
          {navigation.map(item => (
            <NavLink
              key={item.href}
              to={item.href}
              end={item.href === '/'}
              onClick={() => setSidebarOpen(false)}
              className={({ isActive }: { isActive: boolean }) =>
                `flex items-center gap-3 px-3 py-2.5 rounded-lg text-sm font-medium transition-colors ${
                  isActive
                    ? 'bg-sidebar-primary text-sidebar-primary-foreground shadow-sm'
                    : 'text-sidebar-foreground/70 hover:bg-sidebar-accent hover:text-sidebar-accent-foreground'
                }`
              }
            >
              <item.icon size={16} className="shrink-0" />
              {item.name}
            </NavLink>
          ))}
        </nav>
      </aside>

      <div className="lg:pl-64">
        <header className="lg:hidden flex items-center gap-4 px-4 py-3 border-b bg-card sticky top-0 z-30">
          <button
            className="p-2 rounded-lg hover:bg-accent transition-colors"
            onClick={() => setSidebarOpen(true)}
          >
            <Menu size={18} />
          </button>
          <span className="font-semibold text-sm">{APP_TITLE}</span>
        </header>
        <main className="p-6 lg:p-8 max-w-screen-2xl">
          <Outlet />
        </main>
      </div>
    </div>
  );
}

@import "tailwindcss";
@import "tw-animate-css";
@import url('https://fonts.googleapis.com/css2?family=IBM+Plex+Sans:wght@300;400;500;600;700&family=IBM+Plex+Serif:wght@400;600&display=swap');

@custom-variant dark (&:is(.dark *));

@theme inline {
  --radius-sm: calc(var(--radius) - 4px);
  --radius-md: calc(var(--radius) - 2px);
  --radius-lg: var(--radius);
  --radius-xl: calc(var(--radius) + 4px);
  --color-background: var(--background);
  --color-foreground: var(--foreground);
  --color-card: var(--card);
  --color-card-foreground: var(--card-foreground);
  --color-popover: var(--popover);
  --color-popover-foreground: var(--popover-foreground);
  --color-primary: var(--primary);
  --color-primary-foreground: var(--primary-foreground);
  --color-secondary: var(--secondary);
  --color-secondary-foreground: var(--secondary-foreground);
  --color-muted: var(--muted);
  --color-muted-foreground: var(--muted-foreground);
  --color-accent: var(--accent);
  --color-accent-foreground: var(--accent-foreground);
  --color-destructive: var(--destructive);
  --color-border: var(--border);
  --color-input: var(--input);
  --color-ring: var(--ring);
  --color-chart-1: var(--chart-1);
  --color-chart-2: var(--chart-2);
  --color-chart-3: var(--chart-3);
  --color-chart-4: var(--chart-4);
  --color-chart-5: var(--chart-5);
  --color-sidebar: var(--sidebar);
  --color-sidebar-foreground: var(--sidebar-foreground);
  --color-sidebar-primary: var(--sidebar-primary);
  --color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
  --color-sidebar-accent: var(--sidebar-accent);
  --color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
  --color-sidebar-border: var(--sidebar-border);
  --color-sidebar-ring: var(--sidebar-ring);

  /* Custom tokens */
  --color-hero-bg: var(--hero-bg);
  --color-hero-fg: var(--hero-fg);
  --color-stat-indigo: var(--stat-indigo);
  --color-stat-teal: var(--stat-teal);
  --color-stat-amber: var(--stat-amber);
  --color-stat-rose: var(--stat-rose);
  --color-stat-violet: var(--stat-violet);
}

:root {
  --radius: 0.5rem;

  /* Base colors ‚Äî warm off-white background */
  --background: oklch(0.982 0.003 247);
  --foreground: oklch(0.18 0.02 255);
  --card: oklch(1 0 0);
  --card-foreground: oklch(0.18 0.02 255);
  --popover: oklch(1 0 0);
  --popover-foreground: oklch(0.18 0.02 255);

  /* Primary ‚Äî deep indigo */
  --primary: oklch(0.42 0.18 264);
  --primary-foreground: oklch(0.98 0 0);

  --secondary: oklch(0.95 0.01 247);
  --secondary-foreground: oklch(0.3 0.05 264);
  --muted: oklch(0.95 0.005 247);
  --muted-foreground: oklch(0.52 0.02 255);
  --accent: oklch(0.93 0.025 264);
  --accent-foreground: oklch(0.32 0.1 264);
  --destructive: oklch(0.577 0.245 27.325);
  --border: oklch(0.9 0.008 247);
  --input: oklch(0.93 0.007 247);
  --ring: oklch(0.42 0.18 264);

  /* Charts */
  --chart-1: oklch(0.52 0.22 264);
  --chart-2: oklch(0.62 0.16 196);
  --chart-3: oklch(0.72 0.16 52);
  --chart-4: oklch(0.65 0.2 340);
  --chart-5: oklch(0.58 0.18 148);

  /* Sidebar ‚Äî deep navy */
  --sidebar: oklch(0.22 0.06 264);
  --sidebar-foreground: oklch(0.92 0.01 247);
  --sidebar-primary: oklch(0.62 0.22 264);
  --sidebar-primary-foreground: oklch(1 0 0);
  --sidebar-accent: oklch(0.3 0.07 264);
  --sidebar-accent-foreground: oklch(0.95 0.01 247);
  --sidebar-border: oklch(0.32 0.06 264);
  --sidebar-ring: oklch(0.62 0.22 264);

  /* Custom design tokens */
  --hero-bg: oklch(0.42 0.18 264);
  --hero-fg: oklch(0.98 0 0);

  --stat-indigo: oklch(0.52 0.22 264);
  --stat-teal: oklch(0.6 0.14 196);
  --stat-amber: oklch(0.72 0.18 60);
  --stat-rose: oklch(0.65 0.2 8);
  --stat-violet: oklch(0.58 0.2 300);

  --gradient-hero: linear-gradient(135deg, oklch(0.38 0.2 264), oklch(0.28 0.14 280));
  --gradient-card-indigo: linear-gradient(135deg, oklch(0.52 0.22 264), oklch(0.45 0.2 280));
  --gradient-card-teal: linear-gradient(135deg, oklch(0.58 0.15 196), oklch(0.52 0.14 210));
  --gradient-card-amber: linear-gradient(135deg, oklch(0.72 0.18 60), oklch(0.68 0.2 45));
  --gradient-card-rose: linear-gradient(135deg, oklch(0.65 0.2 8), oklch(0.6 0.22 350));
  --gradient-card-violet: linear-gradient(135deg, oklch(0.58 0.2 300), oklch(0.52 0.22 315));
  --shadow-card: 0 4px 20px -4px oklch(0.42 0.18 264 / 0.15);
  --shadow-hero: 0 20px 60px -15px oklch(0.28 0.14 280 / 0.4);
  --transition-smooth: all 0.25s cubic-bezier(0.4, 0, 0.2, 1);
}

.dark {
  --background: oklch(0.145 0 0);
  --foreground: oklch(0.985 0 0);
  --card: oklch(0.205 0 0);
  --card-foreground: oklch(0.985 0 0);
  --popover: oklch(0.205 0 0);
  --popover-foreground: oklch(0.985 0 0);
  --primary: oklch(0.922 0 0);
  --primary-foreground: oklch(0.205 0 0);
  --secondary: oklch(0.269 0 0);
  --secondary-foreground: oklch(0.985 0 0);
  --muted: oklch(0.269 0 0);
  --muted-foreground: oklch(0.708 0 0);
  --accent: oklch(0.269 0 0);
  --accent-foreground: oklch(0.985 0 0);
  --destructive: oklch(0.704 0.191 22.216);
  --border: oklch(1 0 0 / 10%);
  --input: oklch(1 0 0 / 15%);
  --ring: oklch(0.556 0 0);
  --chart-1: oklch(0.488 0.243 264.376);
  --chart-2: oklch(0.696 0.17 162.48);
  --chart-3: oklch(0.769 0.188 70.08);
  --chart-4: oklch(0.627 0.265 303.9);
  --chart-5: oklch(0.645 0.246 16.439);
  --sidebar: oklch(0.205 0 0);
  --sidebar-foreground: oklch(0.985 0 0);
  --sidebar-primary: oklch(0.488 0.243 264.376);
  --sidebar-primary-foreground: oklch(0.985 0 0);
  --sidebar-accent: oklch(0.269 0 0);
  --sidebar-accent-foreground: oklch(0.985 0 0);
  --sidebar-border: oklch(1 0 0 / 10%);
  --sidebar-ring: oklch(0.556 0 0);
}

@layer base {
  * {
    @apply border-border outline-ring/50;
  }
  body {
    @apply bg-background text-foreground;
    font-family: 'IBM Plex Sans', sans-serif;
    font-feature-settings: 'cv11', 'ss01';
  }
  h1, h2, h3, h4 {
    font-family: 'IBM Plex Sans', sans-serif;
    letter-spacing: -0.02em;
  }
}

@layer utilities {
  .gradient-hero {
    background: var(--gradient-hero);
  }
  .gradient-card-indigo {
    background: var(--gradient-card-indigo);
  }
  .gradient-card-teal {
    background: var(--gradient-card-teal);
  }
  .gradient-card-amber {
    background: var(--gradient-card-amber);
  }
  .gradient-card-rose {
    background: var(--gradient-card-rose);
  }
  .gradient-card-violet {
    background: var(--gradient-card-violet);
  }
  .shadow-card {
    box-shadow: var(--shadow-card);
  }
  .shadow-hero {
    box-shadow: var(--shadow-hero);
  }
  .transition-smooth {
    transition: var(--transition-smooth);
  }
}
